import React, { useEffect, useState } from 'react';
import './ProductList.css';

function ProductList() {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        async function fetchProducts() {
            try {
                const response = await fetch('http://localhost:5000/api/products');
                const data = await response.json();

                console.log('Products fetched:', data.result); // Debugging

                setProducts(data.result || []); // Ensure the result array exists
                setLoading(false);
            } catch (error) {
                console.error('Error fetching products:', error);
                setLoading(false);
            }
        }

        fetchProducts();
    }, []);

    if (loading) return <p>Loading products...</p>;

    if (products.length === 0) return <p>No products found.</p>;

    return (
        <div className="product-list">
            {products.map((product) => (
                <div key={product.id} className="product-card">
                    <img
                        src={product.thumbnail_url || '/assets/default-image.png'}
                        alt={product.name}
                        style={{ width: '100%', height: '200px', objectFit: 'cover' }}
                    />
                    <h3>{product.name}</h3>
                    <p>
                        Price: $
                        {product.variants[0]?.price ||
                            product.price ||
                            (product.variants?.[0]?.price || 'N/A')}
                    </p>
                    <a
                        href={product.url || `https://printful.com/product/${product.external_id}`}
                        target="_blank"
                        rel="noopener noreferrer"
                        style={{
                            textDecoration: 'none',
                            background: '#7429A6',
                            color: 'white',
                            padding: '8px 12px',
                            borderRadius: '5px',
                            display: 'inline-block',
                            marginTop: '10px'
                        }}
                    >
                        View Product
                    </a>
                </div>
            ))}
        </div>
    );
}

export default ProductList;
