import React from 'react';
import logo from './assets/logo.png'; // Ensure correct path
import './App.css';

function App() {
    return (
        <div style={styles.container}>
            <img src={logo} alt="App Banner" style={styles.image} />
        </div>
    );
}

const styles = {
    container: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        backgroundColor: '#f4f4f4',
        margin: 0,
        overflow: 'hidden',
    },
    image: {
        width: '90vw', // Take up 90% of the viewport width
        maxWidth: '1200px', // Don’t exceed 1200px width
        height: 'auto', // Maintain aspect ratio
        objectFit: 'contain', // Prevent distortion
        borderRadius: '8px', // Rounded corners
        boxShadow: '0 4px 12px rgba(0, 0, 0, 0.3)', // Add subtle shadow
    },
};

export default App;
